function myFunction (number){
  var y = document.getElementById('canvas');
  var context = y.getContext('2d');
  var centerX = y.width / 2;
  var centerY = y.height / 2;
  var radius = 70;
  context.beginPath();
  context.arc(centerX, centerY, radius, ((-90) * Math.PI)/180, (number * Math.PI)/180, false);
  context.lineWidth = 7;
  context.strokeStyle = 'blue';
  context.font = '15pt Verdana';
  context.fillStyle = 'blue';
  context.fillText("75",centerX-10, centerY+10); 
  context.stroke();
}

